          symnm_i=symnm_d(igrp)
          ic=ichar(symnm_i(1:1))-32
          symnm_i(1:1)=char(ic)
          return
        endif
      enddo
      write(lun,1000)igind
 1000 format(' Group with index ',i8,' has not been found')
      symnm_i='non '
      end
c
      subroutine to_lower(s)
      character*(*) s
c
      do i=1,len(s)
        ic=ichar(s(i:i))
        if(ic.ge.65.and.ic.le.90)then
          ic=ic+32
          s(i:i)=char(ic)
        endif
      enddo
      end
